import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { navigate } from "@reach/router";

export default () => {
  // keeping track of what is being typed
  const [firstname, setfirstname] = useState("");
  const [lastname, setlastname] = useState("");
  const [errors, seterrors] = useState({});

  // handle upon form submition
  const create = (e) => {
    e.preventDefault();
    //const creattheuser = { firstname, lastname };
    //making post request to create a new person
    axios
      .post("http://localhost:8000/api/createuser", {
        firstname,
        lastname,
      })
      .then((res) => {
        console.log(res);
        if (res.data.errors) {
          seterrors(res.data.errors);
        } else {
          navigate("/singleuser/:_id");
        }
      })
      .catch((error) => console.log(error));
  };

  return (
    <div className="container-fluid">
      <div className="row justify-content-center">
        <div className="col-4">
          <div className="card bg-light mb-3">
            <div className="card-header">Register</div>
